import { NodeId } from './constants';

// Number of distance to move when moving nodes.
export const NODES_MOVE_DISTANCE = 5;

/**
 * Structure for guide lines when dragging / moving the nodes.
 */
export interface SnapLine {
    /**
     * snap line: ax + by + c = 0
     * generated by node: (x, y)
     */
    a: number;
    b: number;
    c: number;
    node: NodeId;
    x: number;
    y: number;
}

/**
 * Structure for guide points when dragging / moving the nodes.
 */
export interface SnapPoint {
    // snap to the point.
    x: number;
    y: number;

    // from which nodes the snap point is generated (length === 2).
    originalNodesPos: [{ x: number; y: number }, { x: number; y: number }];
}
